import NavigationBar from './NavigationBar';
import React from "react";
import axios from "axios";
import ItemCard from "./ItemCard";
const apiConfig = require('../api-config');

class Layout extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            searchResults: [],
            searchString: ""
        };

        this.handleSearch = this.handleSearch.bind(this);
    }
    handleSearch(event){
        const target = event.target;
        const value = target.value;
        this.setState({
            searchString: value
        });
        this.doSearch();
    }
    doSearch(){
        const url = apiConfig.serverUrl + '/sales';
        axios.get(url)
            .then((response) => {
                this.setState({
                    searchResults: response.data.sales
                });
            }).catch((error) => {
            console.log(error);
        });
    }

    render() {
        let sales = [];
        if (this.state.searchString){
            sales = this.state.sales.map((sale) =>
                <ItemCard sale={sale} />
            );
        }
        return (
            <div style={{backgroundColor:'whitesmoke', paddingBottom:'30px'}}>
                <NavigationBar page={this.props.page}
                               loggedIn={this.props.loggedIn}
                               user={this.props.user}
                               handleSearch={this.handleSearch} />
                {this.state.searchString ? this.state.searchString : this.props.children}
            </div>
        );
    }
}

export default Layout;
