import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faLiraSign} from "@fortawesome/free-solid-svg-icons";
import Link from "next/link";
import {Button, Fade, Form, FormGroup, Input, InputGroup, InputGroupAddon, InputGroupText} from "reactstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import Countdown from "react-countdown-now";
import Currency from "react-currency-formatter";

class Bidding extends React.Component {
    render() {
        const horizontalRule = (this.props.loggedIn || (this.props.saleOwner || this.props.maxBidder) ) ? <hr/> : "";

        let biddingInput;
        if (this.props.finishedSale || this.props.saleOwner){
            biddingInput = "";
        }else if(!this.props.loggedIn){
            biddingInput =
                <div>
                    <hr/>
                    <h5 className="text-center mb-3">Teklif Ver</h5>
                    <p className="text-center">
                        Teklif vermek için
                        <Link href={'/registration'} >
                            <a className="text-dark underline"> kayıt olun</a>
                        </Link> veya
                        <Link href={'/login'} >
                            <a className="text-dark"> giriş yapın</a>
                        </Link>
                    </p>
                </div>;
        }else if(this.props.maxBidder){
            biddingInput =
                <div>
                    <hr/>
                    <h5 className="text-center mb-3">Teklif Ver</h5>
                    <p> Şu anda en yüksek teklif sizin teklifiniz. </p>
                </div>;
        }else{
            biddingInput = <div>
                <hr/>
                <div className="text-center justify-content-md-center">
                    <h5 className="text-center mb-3">Teklif Ver</h5>
                    <Form>
                        <FormGroup>
                            <InputGroup
                                style={{width:'60%', marginLeft:'20%'}} >
                                <InputGroupAddon addonType="prepend">
                                    <InputGroupText>₺</InputGroupText>
                                </InputGroupAddon>
                                <Input className="text-center"
                                       bgSize="sm"
                                       type="number"
                                       form="nanana"
                                       name="bid"

                                       onChange={this.props.handleInputChange}
                                       onKeyDown={this.props.handleKeyDown}
                                       placeholder="Teklifiniz"/>
                            </InputGroup>
                            <Fade in={this.props.biddingFade.in} tag="p" className="mt-3 text-danger">
                                {this.props.biddingFade.message}
                            </Fade>

                        </FormGroup>
                        <Button onClick={this.props.handleNewBid} color="dark">Gönder</Button>
                    </Form>
                </div>
            </div>;
        }
        let biddingInfo;
        if(!this.props.finishedSale){
            biddingInfo =
                <p className="text-center mt-3 text-success">
                    <Countdown date={this.props.endDate} />
                </p>;
        }else if(this.props.maxBidder){
                biddingInfo =
                    <p className="text-center mt-3 text-success">
                        Kazandınız
                    </p>;
        }else{
            biddingInfo =
                <p className="text-center mt-3 text-danger">
                    Kapandı
                </p>;
        }

        let maxBidInfo = this.props.maxBid.amount in [null, 0] ?
            'Teklif veren olmadı.'
            :
            <span>
                {this.props.maxBid.bidder.nameSurname}
                : <Currency
                    quantity={this.props.maxBid.amount}
                    currency="TRY"
                    pattern="##,### !"
                />
            </span>
            ;
        return (
            <div>
                <h4  className="text-center">En Yüksek Teklif</h4>

                <p className="text-center">
                    {maxBidInfo}
                </p>

                {biddingInput}
                <hr/>

                <div className="row">
                    <div className="col-3 text-center">
                        <span style={{fontSize: '20px', fontFamily:'Verdana', display: 'block'}}>3</span>
                        <span style={{fontSize: '14px', fontFamily:'Courier New'}}>GÜN</span>
                    </div>
                    <div className="col-3 text-center">
                        <span style={{fontSize: '20px', fontFamily:'Verdana', display: 'block'}}>12</span>
                        <span style={{fontSize: '14px', fontFamily:'Courier New'}}>SAAT</span>
                    </div>
                    <div className="col-3 text-center">
                        <span style={{fontSize: '20px', fontFamily:'Verdana', display: 'block'}}>24</span>
                        <span style={{fontSize: '14px', fontFamily:'Courier New'}}>DAKİKA</span>
                    </div>
                    <div className="col-3 text-center">
                        <span style={{fontSize: '20px', fontFamily:'Verdana', display: 'block'}}>48</span>
                        <span style={{fontSize: '14px', fontFamily:'Courier New'}}>SANİYE</span>
                    </div>
                </div>
            </div>
        );
    }
}

export default Bidding;
