import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faLiraSign} from "@fortawesome/free-solid-svg-icons";
import Link from "next/link";
import {Button, Fade, Form, FormGroup, Input, InputGroup, InputGroupAddon, InputGroupText} from "reactstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import Countdown from "react-countdown-now";
import Currency from "react-currency-formatter";

class Bidding extends React.Component {
    render() {
        const horizontalRule = (this.props.loggedIn || (this.props.saleOwner || this.props.maxBidder) ) ? <hr/> : "";

        let biddingInput;
        if (this.props.finishedSale || this.props.saleOwner){
            biddingInput = "";
        }else if(!this.props.loggedIn){
            biddingInput =
                <div>
                    <hr/>
                    <h5 className="text-center mb-3">Teklif Ver</h5>
                    <p className="text-center">
                        Teklif vermek iÃ§in
                        <Link href={'/registration'} >
                            <a className="text-dark underline"> kayÄ±t olun</a>
                        </Link> veya
                        <Link href={'/login'} >
                            <a className="text-dark"> giriÅ yapÄ±n</a>
                        </Link>
                    </p>
                </div>;
        }else if(this.props.maxBidder){
            biddingInput =
                <div>
                    <hr/>
                    <h5 className="text-center mb-3">Teklif Ver</h5>
                    <p> Åu anda en yÃ¼ksek teklif sizin teklifiniz. </p>
                </div>;
        }else{
            biddingInput = <div>
                <hr/>
                <div className="text-center justify-content-md-center">
                    <h5 className="text-center mb-3">Teklif Ver</h5>
                    <Form>
                        <FormGroup>
                            <InputGroup
                                style={{width:'60%', marginLeft:'20%'}} >
                                <InputGroupAddon addonType="prepend">
                                    <InputGroupText>âº</InputGroupText>
                                </InputGroupAddon>
                                <Input className="text-center"
                                       bgSize="sm"
                                       type="number"
                                       form="nanana"
                                       name="bid"

                                       onChange={this.props.handleInputChange}
                                       onKeyDown={this.props.handleKeyDown}
                                       placeholder="Teklifiniz"/>
                            </InputGroup>
                            <Fade in={this.props.biddingFade.in} tag="p" className="mt-3 text-danger">
                                {this.props.biddingFade.message}
                            </Fade>

                        </FormGroup>
                        <Button onClick={this.props.handleNewBid} color="dark">GÃ¶nder</Button>
                    </Form>
                </div>
            </div>;
        }
        let biddingInfo;
        if(!this.props.finishedSale){
            biddingInfo =
                <p className="text-center mt-3 text-success">
                    <Countdown date={this.props.endDate} />
                </p>;
        }else if(this.props.maxBidder){
                biddingInfo =
                    <p className="text-center mt-3 text-success">
                        KazandÄ±nÄ±z
                    </p>;
        }else{
            biddingInfo =
                <p className="text-center mt-3 text-danger">
                    KapandÄ±
                </p>;
        }

        let maxBidInfo = this.props.maxBid.amount in [null, 0] ?
            'Teklif veren olmadÄ±.'
            :
            <span>
                {this.props.maxBid.bidder.nameSurname}
                : <Currency
                    quantity={this.props.maxBid.amount}
                    currency="TRY"
                    pattern="##,### !"
                />
            </span>
            ;
        return (
            <div>
                <h4  className="text-center">En YÃ¼ksek Teklif</h4>

                <p className="text-center">
                    {maxBidInfo}
                </p>

                {biddingInput}
                <hr/>
                {biddingInfo}
                <hr/>
                <div className="row">
                    <div className="col-3 text-center">
                        <span style={{fontSize: '20px', fontFamily:'Verdana', display: 'block'}}>3</span>
                        <span style={{fontSize: '14px', fontFamily:'Courier New'}}>GÃN</span>
                    </div>

                </div>
            </div>
        );
    }
}

export default Bidding;
