import Layout from '../../components/AppLayout';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Form, FormGroup, Input, InputGroup, InputGroupAddon, InputGroupText } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faLiraSign, faUser, faMoneyBill, faUsers, faThumbsUp, faCalendarAlt } from '@fortawesome/free-solid-svg-icons'
import Link from "next/link";
import {auth, getUser} from "../../utils/auth";
import Sale from "../s/[id]";
import {getSale} from "../../utils/api";
import Router from "next/router";

class Purchase extends React.Component{
    constructor(props) {
        super(props);
        let token = this.props.token;
        this.state = {
            loggedIn: !!token,
            user: {},
            sale: defaults.defaultSale,
            maxBid: {
                amount: 0,
                bidder: {
                    email: "",
                    nameSurname: ""
                }
            }
        };
    }
    componentDidMount() {
        getUser(this.props.token).then(user => {
            this.setState({
                user: user
            });
        });
        getSale(this.props.id).then(response => {
            if (!response){
                Router.push('/purchases');
                return;
            }
            if(response.maxBid.bidder.email !== this.state.user.email){
                Router.push('/purchases');
            }
            this.setState({
                sale: response.sale,
                maxBid: response.maxBid
            });
        });
    }
    render() {
        const seller = this.state.sale.owner.nameSurname;
        const salePrice = this.state.maxBid.amount;
        return (
            <Layout page="home">
                <div className="container bg-white" style={{ padding:'3%', marginTop:'3%', boxShadow: '0px 10px 5px 0px #ccc'}} >
                    <h2> Güç Yüzüğü </h2>
                    <hr/>
                    <div className="row">
                        <div className="col-md-5">
                            <img src="/ring.jpeg" width="100%" />
                        </div>
                        <div className="col-md-2"></div>
                        <div className="col-md-3">
                            <p className="mb-5 mt-4">
                                <FontAwesomeIcon icon={faUser} width="16" /> <b>Satıcı: </b> Gamze Gülbahar
                            </p>
                            <p className="mb-5">
                                <FontAwesomeIcon icon={faMoneyBill} width="16" /> <b>Alış fiyatı: </b> 500₺
                            </p>
                            <p className="mb-5">
                                <FontAwesomeIcon icon={faUsers} width="16" /> <b>Teklif sayısı: </b> 13
                            </p>
                            <p className="mb-5">
                                <FontAwesomeIcon icon={faThumbsUp} width="16" /> <b>Durum: </b> Yeni gibi
                            </p>
                            <p className="mb-5">
                                <FontAwesomeIcon icon={faCalendarAlt} width="16" /> <b>Kapanış: </b> 21 Kasım 2019
                            </p>
                        </div>
                    </div>
                </div>
            </Layout>
        );
    }
}

Purchase.getInitialProps = async ctx => {
    const {id} = ctx.query;

    // Check user's session
    const token = auth(ctx);

    return { id, token }
};

export default Purchase;
