import React from "react";

class CountdownNumber {
    render() {
        return (
            <span style={{fontSize: '20px', fontFamily:'Verdana', display: 'block'}}>
                {this.props.number}
            </span>
        );
    }
}
class CountdownTitle {
    render() {
        return (
            <span style={{fontSize: '14px', fontFamily:'Courier New'}}>
                {this.props.title}
            </span>
        );
    }
}
class CountdownBox {
    render() {
        return (
            <div className="col-3 text-center">
                <CountdownNumber number={this.props.number} />
                <CountdownTitle title={this.props.title} />
            </div>
        );
    }
}

class Countdown extends React.Component{
    render() {
        return (
            <div className="row pr-2 pl-2">
                <CountdownBox number="3" title="GÜN" />
                <div className="col-3 text-center">
                    <span style={{fontSize: '20px', fontFamily:'Verdana', display: 'block'}}>12</span>
                    <span style={{fontSize: '14px', fontFamily:'Courier New'}}>SAAT</span>
                </div>
                <div className="col-3 text-center">
                    <span style={{fontSize: '20px', fontFamily:'Verdana', display: 'block'}}>24</span>
                    <span style={{fontSize: '14px', fontFamily:'Courier New'}}>DAKİKA</span>
                </div>
                <div className="col-3 text-center">
                    <span style={{fontSize: '20px', fontFamily:'Verdana', display: 'block'}}>48</span>
                    <span style={{fontSize: '14px', fontFamily:'Courier New'}}>SANİYE</span>
                </div>
            </div>
        );
    }
}

export default Countdown;
