import axios from "axios";
const apiConfig = require('../api-config');

export const getSale = (saleId) => {
    const url = apiConfig.serverUrl + '/sale/' + saleId;
    axios.get(url)
        .then((response) => {
            var maxBid = response.data.sale.bids.length === 0 ? {
                    bidder: {
                        email: "",
                        nameSurname: ""
                    },
                    amount: 0
                }
                :
                response.data.sale.bids.reduce((a, b) => {
                    return a.amount > b.amount ? a : b;
                });
            var sale = response.data.sale;
            sale.isActive = (Date.now() < sale.endDate);
            this.setState({
                sale: sale,
                maxBid: maxBid
            });
        })
        .catch((error) => {
            console.log(error);
        })
};
