import React from "react";
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button
} from 'reactstrap';

// RCE CSS
import 'react-chat-elements/dist/main.css';
// MessageBox component
import { MessageList, MessageBox } from 'react-chat-elements';


class ChatBox extends React.Component{
    render() {
        return (
            <div>
                <Card>
                    <CardImg top width="100%" height="100px"
                             src="https://foreverdogpodcasts.com/wp-content/uploads/2019/07/darktankbg.jpg"
                             alt="Card image cap" />
                    <CardBody>
                        <MessageList
                            className='message-list'
                            lockable={true}
                            toBottomHeight={'100%'}
                            dataSource={[
                                {
                                    position: 'right',
                                    type: 'text',
                                    text: 'Merhaba ürünü ne zaman alabilirsiniz? ',
                                    date: new Date() - 100000,
                                },
                                {
                                    position: 'left',
                                    type: 'text',
                                    text: 'Lorem ipsum dolor sit amet, consectetur adipisicing elit',
                                    date: new Date() - 10000,
                                },
                            ]} />
                    </CardBody>
                </Card>

            </div>
        );
    }
}

export default ChatBox;
