import {Button, Card, CardBody, CardImg, CardSubtitle, CardTitle} from "reactstrap";
import Link from "next/link";

class ItemCard extends React.Component{
    render() {
        let itemName = this.props.name;
        let itemDescription = this.props.description;

        return (
            <div className="col-md-3 mb-5">
                <Card style={{boxShadow: '0px 10px 5px 0px #ccc'}}>
                    <CardImg top width="100%"
                             src={this.props.image}
                             alt="Card image cap" />
                    <CardBody>
                        <CardTitle style={{fontWeight:'bold', fontSize:'22px'}}> {itemName} </CardTitle>
                        <CardSubtitle className="text-muted"> {itemDescription} </CardSubtitle>
                        <hr/>
                        <Link href={'/a/[id]'} as={`/a/1`} >
                            <a className="btn btn-dark text-white">Satışı Gör</a>
                        </Link>
                        <style jsx>{`
                    h1,
                    a {
                      font-family: 'Arial';
                    }

                    ul {
                      padding: 0;
                    }

                    li {
                      list-style: none;
                      margin: 5px 0;
                    }

                    a {
                      text-decoration: none;
                      color: #333;
                    }

                  `}</style>
                    </CardBody>
                </Card>

            </div>
        );
    }
}

export default ItemCard;
